    private void buscarButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buscarButtonActionPerformed
        // TODO add your handling code here:
        // Inicialmente nao ha condicao de busca
        String condition = new String("");
        boolean addedSomething = false;
        
        // Monta a string de condicoes
        // Checa se houve algum atleta selecionado para busca
        if(inserirAtletaDropdown.getSelectedIndex() > 0){
            if(addedSomething)
                condition += " and ";
            else{
                condition += " where ";
                addedSomething = true;
            }
            condition += "atleta = '";
            String atleta = inserirAtletaDropdown.getSelectedItem().toString();
            condition += atleta.split(" ")[2];
            condition += "'";
        }
        // Checa se houve algum medico selecionado para busca
        if(inserirMedicoDropdown.getSelectedIndex() > 0){
            if(addedSomething)
                condition += " and ";
            else{
                condition += " where ";
                addedSomething = true;
            }
            condition += "medico = ";
            String atleta = inserirMedicoDropdown.getSelectedItem().toString();
            condition += atleta.split(" ")[2];
        }
        // Checa se houve algum dia de consulta selecionado para busca
        if(inserirDiaDropdown.getSelectedIndex() > 0){
            if(addedSomething)
                condition += " and ";
            else{
                condition += " where ";
                addedSomething = true;
            }
            condition += "dia = ";
            condition += inserirDiaDropdown.getSelectedItem().toString();
        }
        // Checa se houve algum dia de consulta selecionado para busca
        if(inserirMesDropdown.getSelectedIndex() > 0){
            if(addedSomething)
                condition += " and ";
            else{
                condition += " where ";
                addedSomething = true;
            }
            condition += "mes = ";
            condition += inserirMesDropdown.getSelectedItem().toString();
        }
        // Checa se houve algum dia de consulta selecionado para busca
        if(inserirAnoDropdown.getSelectedIndex() > 0){
            if(addedSomething)
                condition += " and ";
            else{
                condition += " where ";
                addedSomething = true;
            }
            condition += "ano = ";
            condition += inserirAnoDropdown.getSelectedItem().toString();
        }
        // Checa se algo foi escrito para busca na descricao
        if(inserirDescricaoArea.getText().length() > 0){
            
            if(addedSomething)
                condition += " and ";
            else{
                condition += " where ";
                addedSomething = true;
            }
            condition += "descricao like '%";
            condition += inserirDescricaoArea.getText();
            condition += "%'";
        }
        
        System.out.println("select * from lesao_prova" + condition);
        storedStrings[0] = connection.selectCondition("lesao_prova", condition);
        System.out.println("querry results = " + Arrays.toString(storedStrings[0]));
        jTabbedPane1.setSelectedIndex(1);
    }//GEN-LAST:event_buscarButtonActionPerformed

